  logger.info("data setting collection data config version = [{}]", conf.getDataConfigVersion());
  DataConfigSettingCollection.setName(conf.getDataConfigVersion());
  context = new ClassPathXmlApplicationContext("classpath:applicationContext.xml");
  DataConfigService.init(context);
  GameServer.setContext(context);
  playerService = context.getBean(PlayerService.class);
  int uid = 88;
  pr = playerService.getPlayerByUid(GSRuntimeConfig.instance.getServerId(), uid);
  if (pr == null) {
    String nickName = String.format("%s_%s", "gondor111", uid);
    pr = playerService.createNewPlayer(GSRuntimeConfig.instance.getServerId(), uid, nickName);
   } 
}


